mapper.select:
SELECT a.terminal_id AS terminalId ,a.name AS name ,a.longitude AS longitude ,a.latitude AS latitude ,a.create_date AS createDate ,a.user_id AS userId ,c.true_name trueName ,c.nickname ,c.avatar_url avatarUrl ,count(b.id) sensorCount FROM device a LEFT JOIN sensor b ON a.terminal_id = b.terminal_id LEFT JOIN `user` c ON a.user_id = c.id
GROUP BY a.terminal_id
order by ${orderByClause}

SELECT a.terminal_id terminalId, a.name, b.`name` sensorName , b.`code` FROM(
SELECT terminal_id, `name` FROM device WHERE terminal_id = #{terminalId}) a LEFT JOIN sensor b ON a.terminal_id = b.terminal_id
WHERE b.ponds_id = #{pondsId}
