mapper.select:
SELECT a.*, b.`name` pondsName FROM ( SELECT a.*, b.`code`, b.ponds_id pondsId, b.`name` FROM ( SELECT a.*, GROUP_CONCAT(c.name separator '&') as alaram_business_name FROM( SELECT a.id AS id ,a.terminal_id AS terminalId ,a.post_time AS postTime ,a.alaram_business AS alaramBusiness ,a.alaram_equipment AS alaramEquipment, GROUP_CONCAT(b.name separator '&') as alaram_equipment_name FROM device_warn_record a LEFT JOIN device_warn_type b ON INSTR(a.alaram_equipment, b.`code`) > 0 GROUP BY a.id) a LEFT JOIN device_warn_type c ON INSTR(a.alaramBusiness, c.`code`) > 0 GROUP BY a.id LIMIT #{begin}, #{end}) a LEFT JOIN sensor b ON a.terminalId = b.terminal_id AND INSTR(a.alaramBusiness, b.`code`) > 0) a LEFT JOIN ponds b ON a.pondsId = b.id WHERE 1=1
AND b.user_id = #{entity.userId}
AND b.id = #{entity.pondsId}
order by ${orderByClause}

SELECT DISTINCT a.* FROM ( SELECT a.*, GROUP_CONCAT(c. NAME SEPARATOR '&') AS alaram_business_name FROM ( SELECT a.id AS id , a.terminal_id AS terminalId , a.post_time AS postTime , a.alaram_business AS alaramBusiness , a.alaram_equipment AS alaramEquipment , GROUP_CONCAT(b. NAME SEPARATOR '&') AS alaram_equipment_name FROM device_warn_record a LEFT JOIN device_warn_type b ON INSTR(a.alaram_equipment , b.`code`) > 0 GROUP BY a.id ) a LEFT JOIN device_warn_type c ON INSTR(a.alaramBusiness , c.`code`) > 0 GROUP BY a.id ) a , ( SELECT b.terminal_id , b.`code` FROM ponds a , sensor b , `user` c WHERE a.id = b.ponds_id AND a.user_id = c.id
AND c.openid = #{entity.openid}
) b WHERE a.terminalId = b.terminal_id AND INSTR(a.alaramBusiness , b.`code`) > 0

SELECT COUNT(0) FROM ( SELECT a.*, b.`name` pondsName FROM ( SELECT a.*, b.`code`, b.ponds_id pondsId, b.`name` FROM ( SELECT a.*, GROUP_CONCAT(c.name separator '&') as alaram_business_name FROM( SELECT a.id AS id ,a.terminal_id AS terminalId ,a.post_time AS postTime ,a.alaram_business AS alaramBusiness ,a.alaram_equipment AS alaramEquipment, GROUP_CONCAT(b.name separator '&') as alaram_equipment_name FROM device_warn_record a LEFT JOIN device_warn_type b ON INSTR(a.alaram_equipment, b.`code`) > 0 GROUP BY a.id) a LEFT JOIN device_warn_type c ON INSTR(a.alaramBusiness, c.`code`) > 0 GROUP BY a.id LIMIT 0, 100000) a LEFT JOIN sensor b ON a.terminalId = b.terminal_id AND INSTR(a.alaramBusiness, b.`code`) > 0) a LEFT JOIN ponds b ON a.pondsId = b.id
WHERE b.user_id = #{userId}
GROUP BY id) a
